import{_ as o,c as e,o as t,a as p}from"./app.4f6daae5.js";const d=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"record/book_RDZS.md"}'),c={name:"record/book_RDZS.md"},_=p("<blockquote><p>\u4F60\u6240\u7ED9\u4E88\u51F6\u624B\u7684\u4EC1\uFF0C\u4FBF\u662F\u523A\u5411\u6B7B\u8005\u7684\u6076\u3002</p></blockquote><blockquote><p>\u5706\u6ED1\u4E16\u6545\u672A\u5FC5\u662F\u6210\u957F\uFF0C\u4F46\u5BBD\u5BB9\u4E00\u5B9A\u662F\u3002</p></blockquote><blockquote><p>\u6587\u5B57\u7684\u6838\u5FC3\uFF0C\u662F\u5DE5\u5177\u6027\u3002\u4EBA\u65CF\u662F\u5148\u6709\u8BED\u8A00\uFF0C\u540E\u6709\u6587\u5B57\u3002\u8BED\u8A00\u672C\u8EAB\u5C31\u662F\u4E00\u79CD\u5DE5\u5177\uFF0C\u662F\u4EA4\u6D41\u7684\u6700\u57FA\u672C\u5DE5\u5177\u3002\u800C\u6587\u5B57\uFF0C\u4E5F\u662F\u4E00\u79CD\u5DE5\u5177\uFF0C\u627F\u8F7D\u4FE1\u606F\uFF0C\u8BA9\u6211\u4EEC\u51C6\u786E\u4EA4\u6D41\u3002\u4F46\u6587\u5B57\u9664\u4E86\u5DE5\u5177\u6027\uFF0C\u8FD8\u6709\u827A\u672F\u6027\uFF0C\u6BD4\u5982\u4E66\u6CD5\u5927\u5BB6\uFF0C\u4FBF\u80FD\u5C55\u73B0\u6587\u5B57\u7F8E\u7684\u6781\u81F4\u3002\u53EA\u4E0D\u8FC7\uFF0C\u827A\u672F\u6027\u53EA\u662F\u6587\u5B57\u7684\u6B21\u8981\u7279\u6027\uFF0C\u8FDC\u8FDC\u6BD4\u4E0D\u4E0A\u6838\u5FC3\u3002</p></blockquote><blockquote><p>\u6211\u65E0\u6CD5\u4FDD\u8BC1\u6211\u505A\u7684\u4E8B\u662F\u5584\u826F\u548C\u6B63\u786E\u7684\uFF0C\u4E5F\u65E0\u6CD5\u4FDD\u8BC1\u9075\u5FAA\u4EC1\u4E49\u9053\u5FB7\u793C\u6CD5\u3002\u6211\u727A\u7272\u4E86\u4ED6\u4EEC\uFF0C\u4F46\u6211\u53EF\u4EE5\u4FDD\u8BC1\uFF0C\u6211\u6BD4\u6BCF\u4E2A\u4EBA\u7684\u727A\u7272\u90FD\u591A\uFF0C\u6211\u6BD4\u6BCF\u4E2A\u4EBA\u7684\u4ED8\u51FA\u90FD\u591A\uFF0C\u6211\u4E0D\u4F1A\u8E0F\u7740\u4ED6\u4EEC\u7684\u810A\u6881\u5439\u5618\u6211\u7684\u529F\u7EE9\uFF0C\u6211\u4E0D\u4F1A\u8BA9\u4ED6\u4EEC\u5641\u5FC3\uFF0C\u4E0D\u4F1A\u8BA9\u4ED6\u4EEC\u4E3A\u4E86\u751F\u8BA1\u53BB\u51FA\u5356\u81EA\u5DF1\u7684\u8EAB\u4F53\u2026\u2026\u727A\u7272\u4ED6\u4EEC\u7684\u539F\u56E0\uFF0C\u662F\u6211\u4EEC\u4EBA\u65CF\u9AD8\u5C42\u7684\u65E0\u80FD\u4E0E\u65E0\u803B\uFF0C\u6211\u6700\u540E\u7684\u5E95\u7EBF\u4FBF\u662F\uFF0C\u6211\u53EF\u4EE5\u727A\u7272\u4ED6\u4EEC\uFF0C\u4F46\u6211\u4E0D\u4F1A\u5148\u628A\u4ED6\u4EEC\u5BA3\u4F20\u6210\u61D2\u60F0\u3001\u8D2A\u5A6A\u548C\u611A\u6627\u7684\u56E2\u4F53\uFF0C\u7136\u540E\u4EE5\u6B63\u4E49\u7684\u540D\u4E49\u628A\u4ED6\u4EEC\u903C\u51FA\u519C\u7530\uFF0C\u903C\u51FA\u6751\u9547\uFF0C\u903C\u5230\u57CE\u5E02\u7684\u5DE5\u574A\u3002\u5C31\u5982\u540C\uFF0C\u82E5\u662F\u54EA\u4E00\u5929\u6211\u9700\u8981\u4ED6\u4EEC\u518D\u6B21\u727A\u7272\uFF0C\u9700\u8981\u4ED6\u4EEC\u4ECE\u5DE5\u574A\u91CC\u79BB\u5F00\uFF0C\u6211\u4E5F\u4E0D\u4F1A\u8BA9\u4ED6\u4EEC\u80CC\u4E0A\u672C\u5E94\u8BE5\u7531\u6211\u4EEC\u8FD9\u4E9B\u65E0\u80FD\u65E0\u803B\u9AD8\u5C42\u80CC\u8D1F\u7684\u9A82\u540D\u3002</p></blockquote><blockquote><p>\u4EBA\u65CF\uFF0C\u4F20\u627F\u7684\u662F\u7CBE\u795E\uFF0C\u4F20\u627F\u7684\u662F\u6587\u660E\uFF0C\u4F20\u627F\u7684\u662F\u77E5\u8BC6\u3002\u6211\u6CA1\u6709\u8D44\u683C\u5224\u51B3\u4ECA\u5929\u7684\u5112\u5BB6\u662F\u4E0D\u662F\u5112\u5BB6\uFF0C\u6211\u4E5F\u6CA1\u8D44\u683C\u5224\u51B3\u4EE5\u540E\u7684\u5112\u5BB6\u662F\u4E0D\u662F\u5112\u5BB6\uFF0C\u4F46\u53EA\u8981\u6211\u4EEC\u4E00\u76F4\u5728\u8FDB\u6B65\uFF0C\u4E00\u76F4\u5728\u5B66\u4E60\uFF0C\u4E00\u76F4\u5728\u52AA\u529B\uFF0C\u751A\u81F3\u4E8E\uFF0C\u54EA\u6015\u6211\u4EEC\u7684\u540E\u4EE3\u5FD8\u8BB0\u2018\u5112\u5BB6\u2019\u8FD9\u4E2A\u8BCD\uFF0C\u4F46\u6211\u4F9D\u65E7\u6562\u8BF4\uFF0C\u90A3\u662F\u5112\u5BB6\uFF01</p></blockquote><blockquote><p>\u201C\u6211\u4EEC\u628A\u5386\u53F2\u60F3\u8C61\u6210\u6A2A\u5728\u773C\u524D\u6709\u523B\u5EA6\u7684\u5C3A\u5B50\uFF0C\u5C31\u4F1A\u53D1\u73B0\uFF0C\u5728\u897F\u5468\u65F6\u671F\uFF0C\u793C\u4E50\u5982\u714C\u714C\u592A\u9633\uFF0C\u7167\u4EAE\u897F\u5468\u6240\u6709\u7684\u523B\u5EA6\u3002\u4F46\u662F\uFF0C\u5230\u4E86\u4E1C\u5468\u65F6\u671F\uFF0C\u4E5F\u5C31\u662F\u6625\u79CB\u6218\u56FD\u65F6\u671F\uFF0C\u968F\u7740\u5C3A\u5B50\u5EF6\u4F38\uFF0C\u793C\u4E50\u592A\u9633\u5E76\u6CA1\u6709\u53D8\uFF0C\u5BF9\uFF0C\u5B83\u5E76\u6CA1\u6709\u53D8\u5F31\uFF01\u4F46\u662F\u5728\u6625\u79CB\u65F6\u671F\uFF0C\u5B54\u5723\u7684\u65F6\u671F\uFF0C\u5347\u8D77\u4E86\u65B0\u7684\u592A\u9633\uFF0C\u8FD9\u8F6E\u5112\u5BB6\u592A\u9633\uFF0C\u6BD4\u793C\u4E50\u5927\u65E5\u4EAE\u767E\u500D\u5343\u500D\uFF01\u201D</p><p>\u201C\u6625\u79CB\u65F6\u671F\u7684\u4EBA\u4EEC\uFF0C\u4F9D\u65E7\u53EF\u4EE5\u611F\u53D7\u5230\u793C\u4E50\u7684\u5149\u8292\uFF0C\u4F46\u6625\u79CB\u6218\u56FD\u6700\u5149\u8F89\u7684\u5B58\u5728\uFF0C\u662F\u5112\u5BB6\u7684\u592A\u9633\uFF01\u6211\u4EEC\u5E94\u8BE5\u53BB\u5438\u6536\u5112\u5BB6\u592A\u9633\u7684\u5149\u8F89\uFF0C\u800C\u4E0D\u662F\u611A\u8822\u5730\u53BB\u5438\u6536\u793C\u4E50\u592A\u9633\u7684\u5149\u8292\u3002\u201D</p><p>\u201C\u793C\u4E50\u601D\u60F3\u5F88\u4F1F\u5927\uFF0C\u793C\u4E50\u7684\u592A\u9633\uFF0C\u4F9D\u65E7\u80FD\u7167\u8000\u6211\u4EEC\u3002\u5F53\u6211\u4EEC\u63A5\u53D7\u4E86\u5112\u5BB6\u592A\u9633\u7684\u7167\u8000\u540E\uFF0C\u6211\u4EEC\u8EAB\u4E0A\u867D\u7136\u4F9D\u65E7\u6709\u793C\u4E50\u7684\u5149\u8F89\uFF0C\u4F46\u662F\uFF0C\u6211\u4EEC\u6BCF\u4E00\u4E2A\u4EBA\uFF0C\u5DF2\u7ECF\u6BD4\u793C\u4E50\u592A\u9633\u66F4\u707F\u70C2\uFF01\u201D</p><p>\u201C\u800C\u5728\u4E0D\u4E45\u7684\u672A\u6765\uFF0C\u5728\u4EBA\u7C7B\u5386\u53F2\u7684\u523B\u5EA6\u5C3A\u4E0A\uFF0C\u5FC5\u7136\u4F1A\u51FA\u73B0\u6BD4\u5112\u5BB6\u592A\u9633\u707F\u70C2\u767E\u500D\u5343\u500D\u7684\u65B0\u592A\u9633\uFF0C\u800C\u90A3\u65F6\u5019\u7684\u4EBA\u65CF\uFF0C\u4F9D\u65E7\u6CBE\u67D3\u5112\u5BB6\u592A\u9633\u7684\u5149\u8292\uFF0C\u4F46\u4ED6\u4EEC\u6BCF\u4E00\u4E2A\u4EBA\u90FD\u6BD4\u5112\u5BB6\u592A\u9633\u66F4\u5149\u8F89\uFF01\u201D</p><p>\u201C\u793C\u4E50\u6CA1\u9519\uFF0C\u4EC1\u653F\u6CA1\u6709\u9519\uFF0C\u5112\u5BB6\u6CA1\u6709\u9519\uFF0C\u5B83\u4EEC\u90FD\u662F\u67D0\u4E00\u4E2A\u65F6\u4EE3\u6700\u5149\u8F89\u7684\u5B58\u5728\u3002\u4F46\u662F\uFF0C\u5982\u679C\u6211\u4EEC\u6C38\u8FDC\u7559\u604B\u4ED6\u4EEC\u5149\u8292\u7684\u6E29\u6696\uFF0C\u4E0D\u53BB\u8FFD\u5BFB\u6216\u521B\u9020\u65B0\u7684\u592A\u9633\uFF0C\u90A3\u4E48\uFF0C\u6211\u4EEC\u4E00\u5B9A\u9519\u4E86\u3002\u201D</p><p>\u201C\u6240\u4EE5\uFF0C\u89C1\u4E0D\u8D24\u800C\u5185\u81EA\u7701\u662F\u8BF4\uFF0C\u6211\u4EEC\u8981\u770B\u770B\u81EA\u5DF1\u8EAB\u4E0A\uFF0C\u6709\u6CA1\u6709\u548C\u5148\u8F88\u4E00\u6837\u7684\u9519\u8BEF\uFF0C\u54EA\u6015\u7528\u5200\u628A\u8FD9\u79CD\u9519\u8BEF\u5272\u6389\uFF0C\u54EA\u6015\u9C9C\u8840\u6DCB\u6F13\uFF0C\u6211\u4EEC\u4E5F\u5E94\u8BE5\u53BB\u505A\uFF01\u4E0D\u7136\uFF0C\u5F53\u5148\u8D24\u9519\u8BEF\u7684\u5DE8\u7624\u4E0D\u65AD\u589E\u591A\uFF0C\u603B\u6709\u4E00\u5929\u4F1A\u541E\u566C\u6211\u4EEC\u7684\u8EAB\u4F53\uFF0C\u4E00\u8D77\u706D\u4EA1\uFF01\u201D</p><p>\u201C\u8BA4\u6E05\u65B9\u5411\uFF0C\u8BA4\u6E05\u5B66\u4EC0\u4E48\uFF0C\u6BD4\u5B66\u4E60\u548C\u52AA\u529B\u672C\u8EAB\u66F4\u52A0\u91CD\u8981\uFF01\u201D</p></blockquote><blockquote><p>\u2014 \u5982\u679C\u60A8\u773C\u524D\u53EA\u770B\u5230\u767D\u96EA\u7691\u7691\uFF0C\u53C8\u5F53\u5982\u4F55\uFF1F</p><p>\u2014 \u81EA\u4F1A\u6709\u4EBA\u643A\u65B0\u7DD1\u767B\u95E8\uFF01</p></blockquote><blockquote><p>\u4ECA\u65E5\u7684\u65B0\u706B\uFF0C\u5E76\u672A\u906E\u6321\u8FC7\u53BB\u7684\u5149\u8F89\u3002</p><p>\u65E7\u65E5\u7684\u661F\u8FB0\uFF0C\u4EA6\u4E0D\u5E94\u4E0E\u65ED\u65E5\u4E89\u8F89\u3002</p></blockquote><blockquote><p>\u9053\u4E0D\u65A9\u6211\uFF0C\u6211\u65A9\u6211\u3002</p></blockquote><blockquote><p>\u65E0\u9053\u5219\u89C1\uFF0C\u6709\u9053\u5219\u9690</p><p>\u6CE8\uFF1A\u65E0\u65B0\u9053\uFF0C\u5219\u73B0\u3002\u6709\u65B0\u9053\uFF0C\u5219\u9690\u3002\u9769\u65B0\u81EA\u6211\uFF0C\u9F0E\u9769\u5929\u4E0B\u3002</p></blockquote>",10),l=[_];function k(u,a,b,r,s,q){return t(),e("div",null,l)}const i=o(c,[["render",k]]);export{d as __pageData,i as default};
